package form;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import ru.ystu.nx.entity.Queue;import ru.ystu.nx.service.QueueService;import ru.ystu.nx.service.QueueServiceImpl;public class MainFormImpl implements MainForm {	private final QueueServiceImpl queueService;	public MainFormImpl(QueueServiceImpl queueService) {		this.queueService = queueService;	}	@Override	public void startQuiz() {		ApplicationContext context = new ClassPathXmlApplicationContext("main.xml");		QueueServiceImpl service = context.getBean(QueueServiceImpl.class);		service.run();	}}