package ru.ystu.nx;import form.MainFormImpl;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.support.ClassPathXmlApplicationContext;@ComponentScanclass Main {	public static void main(String[] args) {		ApplicationContext context = new ClassPathXmlApplicationContext("main.xml");		MainFormImpl service = context.getBean(MainFormImpl.class);		service.startQuiz();	}}