package ru.ystu.nx;import form.MainFormImpl;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.support.ClassPathXmlApplicationContext;import ru.ystu.nx.service.QueueServiceImpl;@ComponentScanclass Main {	public static void main(String[] args) {		ApplicationContext context = new ClassPathXmlApplicationContext("main.xml");		QueueServiceImpl service = context.getBean(QueueServiceImpl.class);		new MainFormImpl(service).startQuiz();	}}